import React, { useEffect } from "react";
import { Button as MuiButton } from "@mui/material";
import { Button } from "@chakra-ui/react";
import { ArrowForwardIcon } from "@chakra-ui/icons";
import { css, keyframes } from "@emotion/css";

const animationScaleUp = keyframes`
  0% {
    transform: scale(0.5);
  }
  100% {
    transform: scale(1);
  }
`;

const HomePage = () => {
  useEffect(() => {
    const scrollAnimElements = document.querySelectorAll(
      "[data-animate-on-scroll]"
    );
    const observer = new IntersectionObserver(
      (entries) => {
        for (const entry of entries) {
          if (entry.isIntersecting || entry.intersectionRatio > 0) {
            const targetElement = entry.target;
            targetElement.classList.add("animate");
            observer.unobserve(targetElement);
          }
        }
      },
      {
        threshold: 0.15,
      }
    );

    for (let i = 0; i < scrollAnimElements.length; i++) {
      observer.observe(scrollAnimElements[i]);
    }

    return () => {
      for (let i = 0; i < scrollAnimElements.length; i++) {
        observer.unobserve(scrollAnimElements[i]);
      }
    };
  }, []);

  return (
    <div
      className={css`
        position: relative;
        border-radius: var(--br-5xl);
        background-color: black;
        max-width: 1440px;
        margin: 0 auto;
        padding: 0 20px; /* Add padding to center content */
        text-align: left;
        font-size: 24px; /* Adjust the font size for desktop */
        color: var(--white-color);
        font-family: var(--font-lexend);
        &.animate {
          animation: 1s ease 0s 1 normal forwards ${animationScaleUp};
        }
      `}
      data-animate-on-scroll
    >
      <img
        className={css`
          position: absolute;
          top: 0px;
          right: 0px;
          width: 1920px;
          height: 1080px;
          object-fit: cover;
        `}
        alt=""
        src="/rectangle-4@2x.png"
      />
      <img
        className={css`
          position: absolute;
          top: 128px;
          left: 913px;
          border-radius: var(--br-231xl) var(--br-131xl) var(--br-231xl)
            var(--br-131xl);
          width: 874px;
          height: 776px;
          object-fit: cover;
        `}
        alt=""
        src="/pngtransparenthrmanagerwelcomesnewemployeeremovebg-1@2x.png"
      />
      <div
        className={css`
          position: absolute;
          top: 78px;
          left: 96px;
          width: 817px;
          height: 826px;
        `}
      >
        <div
          className={css`
            position: absolute;
            top: 0px;
            left: 117px;
            font-weight: 500;
            display: inline-block;
            width: 527px;
            height: 153px;
          `}
        >
          Higher Me
        </div>
        <div
          className={css`
            position: absolute;
            top: 559px;
            left: 121px;
            width: 456px;
            height: 187px;
          `}
        >
          <a
            className={css`
              position: absolute;
              top: 112px;
              left: 189px;
            `}
            href="./signup"
          >
            <Button
              variant="solid"
              w="267px"
              colorScheme="pink"
              size="lg"
              rightIcon={<ArrowForwardIcon />}
            >
              Sign Up
            </Button>
          </a>
          <div
            className={css`
              position: absolute;
              top: 0px;
              left: 0px;
              width: 267px;
            `}
          />
        </div>
        <a
          className={css`
            position: absolute;
            top: 570px;
            left: 121px;
          `}
          href="/login"
        >
          <MuiButton sx={{ width: 267 }} variant="contained" color="secondary" size="large">
            Login
          </MuiButton>
        </a>
        <div
          className={css`
            position: absolute;
            top: 198px;
            left: 0px;
            border-radius: 104.5px;
            border: 7px solid var(--white-color);
            box-sizing: border-box;
            width: 799px;
            height: 162px;
            overflow: hidden;
            display: flex;
            flex-direction: column;
            padding: var(--padding-0) var(--padding-0) var(--padding-0) 1px;
            align-items: flex-end;
            justify-content: center;
            font-size: 35px;
          `}
        >
          <div
            className={css`
              align-self: stretch;
              position: relative;
              font-weight: 500;
            `}
          >
            <span>{`Hire the best `}</span>
            <span
              className={css`
                color: var(--color-aqua);
              `}
            >
              HR representatives
            </span>
            <span>{` and `}</span>
            <span
              className={css`
                color: var(--color-aqua);
              `}
            >
              Managers
            </span>
            <span> to take your Company Higher.</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
